/// <reference types="node" />
import { ILyric } from './declare';
/**
 * 解密LRCX歌词
 * @param buffer 歌词内容
 * @returns 解密后的歌词，解密失败返回null
 */
export declare function decrypt(buffer: Buffer): string | null;
/**
 * 编码歌词
 * @param content 歌词的文本内容
 */
export declare function encrypt(content: string | Buffer): Buffer;
/**
 * 解析LRCX歌词
 * @param lrcxText LRCX歌词文本内容
 */
export declare function parse(lrcxText: string): import("./declare").IKaraOKLyric;
/**
 * 将LRCX歌词转换为文本
 *
 * _注意：如果没有设置lyric.ext.kuwo,系统会自动生成一个_
 *
 * @param lyric 歌词(只能是卡拉OK歌词)
 */
export declare function stringify(lyric: ILyric): string;
